import React from 'react';
import '../styles/Home.css'; // Assuming you have a CSS file for additional styles

export default function Home() {
  return (
    <div
      style={{
        backgroundImage: `
          linear-gradient(to right bottom, rgba(11, 36, 162, 0.5), rgba(40, 180, 133, 0.8)),
          url('https://images.pexels.com/photos/2990644/pexels-photo-2990644.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1')
        `,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '100vh', // Adjust this to your desired height
        color: 'white',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        textAlign: 'center',
      }}
    >
      <h1>Make Your Voice Count</h1>
      <div>
        <p className="homepage-message">
          iReporter was created to empower citizens to report corruption and incidents requiring government intervention. Our platform aims to ensure transparency, accountability, and swift action on pressing issues.
        </p>
      </div>
    </div>
  );
}
